var mysql = require('mysql');
var q = require('q');

var config = require('../../../config');
var log = require('./logger').logger();

////////////////////////
//  PUBLIC FUNCTIONS  //
////////////////////////

/**
 * Function that initializes a connection with the database
 * @see https://github.com/felixge/node-mysql
 *
 * @param  {Object}     extraParams     Extra parameters for the connection
 */
var connectWithDB = module.exports.connectWithDB = function(extraParams) {
    var deferred = q.defer();

    // Try setting up a new connection
    try {

        var settings = config.secret.libraries.db;
        var connection = mysql.createConnection({
          'host': settings.host,
          'database': settings.database,
          'user': settings.user,
          'password': settings.password,
          'multipleStatements': extraParams.multipleStatements || false
        });

        // Connect with the MySQL database server
        connection.connect(function(err) {
            if (err) {
                log().error({'code': 500, 'msg': err}, 'Error while setting up a database connection');
                return deferred.reject({'code': 500, 'msg': 'Error while setting up a database connection'});
            }

            // Resolve the promise by returning the connection
            return deferred.resolve(connection);
        });

    // Reject the promise when setting up a connection fails
    } catch(err) {
        log().error({'code': 500, 'msg': err}, 'Error while setting up a database connection');
        return deferred.reject({'code': 500, 'msg': 'Error while setting up a database connection'});
    }

    // Return a promise
    return deferred.promise;
};

